import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PodData } from '../types';
import PodCard from '../components/PodCard';
import { getLatestPodData } from '../utils/helpers';

interface PodWithAction extends PodData {
  actionLog: string;
}

const getCityActionLogKey = (city: string) => `city-action-log-${city}`;
const getPodActionLogKey = (podId: string) => `pod-action-log-${podId}`;

const CityPods: React.FC = () => {
  const { cityName } = useParams<{ cityName: string }>();
  const navigate = useNavigate();
  const [pods, setPods] = useState<PodWithAction[]>([]);
  const [cityOwner, setCityOwner] = useState<{ name: string; email: string } | null>(null);
  const [lastUpdated, setLastUpdated] = useState<string>('');
  const [cityO2har, setCityO2har] = useState<number>(0);
  const [cityUnserviceability, setCityUnserviceability] = useState<number>(0);
  const [breachedPods, setBreachedPods] = useState<number>(0);
  const [cityActionLog, setCityActionLog] = useState<string>('');
  const [, setLoading] = useState(true);

  useEffect(() => {
    const fetchPods = async () => {
      try {
        const response = await fetch('/data/pods.json');
        const data: PodData[] = await response.json();
        // Get latest pod per pod_id for this city
        const podMap = new Map<string, PodData>();
        data.filter(p => p.city === cityName).forEach(pod => {
          if (!podMap.has(pod.pod_id) || new Date(pod.date) > new Date(podMap.get(pod.pod_id)!.date)) {
            podMap.set(pod.pod_id, pod);
          }
        });
        const podsArr = Array.from(podMap.values());
        // Add action logs from localStorage
        const podsWithAction: PodWithAction[] = podsArr.map(pod => ({
          ...pod,
          actionLog: localStorage.getItem(getPodActionLogKey(pod.pod_id)) || '',
        }));
        setPods(podsWithAction);
        if (podsArr.length > 0) {
          setCityOwner({ name: podsArr[0].city_owner_name, email: podsArr[0].city_owner_email });
          // Aggregate city metrics
          let o2har = 0, unserviceability = 0, breached = 0, last = podsArr[0].date;
          podsArr.forEach(pod => {
            o2har += pod.o2har;
            unserviceability += pod.unserviceability;
            if (pod.o2har > 9.0 || pod.unserviceability > 5.0) breached++;
            if (new Date(pod.date) > new Date(last)) last = pod.date;
          });
          setCityO2har(o2har / podsArr.length);
          setCityUnserviceability(unserviceability / podsArr.length);
          setBreachedPods(breached);
          setLastUpdated(new Date(last).toLocaleString());
        }
        setCityActionLog(localStorage.getItem(getCityActionLogKey(cityName!)) || '');
      } catch (e) {
        setPods([]);
      } finally {
        setLoading(false);
      }
    };
    if (cityName) fetchPods();
  }, [cityName]);

  const handlePodActionChange = (podId: string, value: string) => {
    setPods(prev => prev.map(p => p.pod_id === podId ? { ...p, actionLog: value } : p));
    localStorage.setItem(getPodActionLogKey(podId), value);
  };

  const handleCityActionChange = (value: string) => {
    setCityActionLog(value);
    localStorage.setItem(getCityActionLogKey(cityName!), value);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-neutral-50 to-primary-50 dark:from-neutral-900 dark:to-secondary-900">
      {/* Header */}
      <div className="bg-white/80 dark:bg-secondary-800/80 backdrop-blur-md border-b border-neutral-200 dark:border-secondary-700 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-14 h-14 bg-gradient-to-br from-primary-500 to-accent-500 rounded-2xl flex items-center justify-center shadow-medium">
                <span className="text-2xl text-white">🏙️</span>
              </div>
              <div>
                <button 
                  onClick={() => navigate('/')} 
                  className="text-secondary-600 dark:text-secondary-400 hover:text-primary-600 dark:hover:text-primary-400 mb-2 flex items-center text-sm font-medium transition-colors"
                >
                  ← Back to All Cities
                </button>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-primary-600 to-accent-600 bg-clip-text text-transparent">
                  {cityName}
                </h1>
                {cityOwner && (
                  <p className="text-secondary-600 dark:text-secondary-400 text-sm mt-1">
                    Owner: {cityOwner.name} (
                    <a 
                      href={`mailto:${cityOwner.email}`} 
                      className="text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 underline transition-colors"
                    >
                      {cityOwner.email}
                    </a>
                    )
                  </p>
                )}
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <div className="text-sm text-secondary-500 dark:text-secondary-400">Last Updated</div>
                <div className="text-xs text-secondary-400 dark:text-secondary-500">{lastUpdated}</div>
                <div className="text-xs text-secondary-400 dark:text-secondary-500">(hourly)</div>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {/* City Metrics Summary */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
          <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 flex flex-col items-center hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
            <span className="text-sm text-secondary-500 dark:text-secondary-400 mb-2 font-medium">O2HAR</span>
            <span className={`text-3xl font-bold ${cityO2har > 9.0 ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>{cityO2har.toFixed(1)} Mins</span>
            <span className="text-xs text-secondary-400 dark:text-secondary-500">Threshold: 9.0 Mins</span>
          </div>
          <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 flex flex-col items-center hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
            <span className="text-sm text-secondary-500 dark:text-secondary-400 mb-2 font-medium">Unserviceability</span>
            <span className={`text-3xl font-bold ${cityUnserviceability > 5.0 ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>{cityUnserviceability.toFixed(1)}%</span>
            <span className="text-xs text-secondary-400 dark:text-secondary-500">Threshold: 5.0%</span>
          </div>
          <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 flex flex-col items-center hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
            <span className="text-sm text-secondary-500 dark:text-secondary-400 mb-2 font-medium">Pods Breaching</span>
            <span className="text-3xl font-bold text-error-600 dark:text-error-400">{breachedPods}</span>
            <span className="text-xs text-secondary-400 dark:text-secondary-500">of {pods.length} pods</span>
          </div>
        </div>

        {/* City Action Log */}
        <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 mb-6 border border-neutral-200/50 dark:border-secondary-700/50">
          <label className="block text-sm font-medium text-secondary-700 dark:text-secondary-300 mb-3">City Owner Action Log</label>
          <textarea
            className="w-full px-4 py-3 border border-neutral-200 dark:border-secondary-600 dark:bg-secondary-700/50 dark:text-white rounded-xl focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent placeholder-secondary-400 dark:placeholder-secondary-500"
            rows={3}
            value={cityActionLog}
            onChange={e => handleCityActionChange(e.target.value)}
            placeholder="What is being done at the city level?"
          />
        </div>
      </div>

      {/* Pods List */}
      <div className="max-w-7xl mx-auto px-6 pb-8">
        <h2 className="text-xl font-bold text-secondary-900 dark:text-white mb-6">Pods in {cityName}</h2>
        <div className="space-y-6">
          {pods.map(pod => (
            <div key={pod.pod_id} className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft flex flex-col md:flex-row items-start md:items-center p-6 gap-6 border border-neutral-200/50 dark:border-secondary-700/50">
              <div className="flex-1 min-w-0">
                <div className="flex items-center justify-between mb-3">
                  <div>
                    <span className="text-lg font-semibold text-secondary-900 dark:text-white">{pod.pod_name}</span>
                    <span className="ml-3 text-sm text-secondary-500 dark:text-secondary-400">{pod.zone}</span>
                  </div>
                  <button onClick={() => navigate(`/pod/${pod.pod_id}`)} className="text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 text-sm font-medium underline transition-colors">Details</button>
                </div>
                <div className="text-sm text-secondary-500 dark:text-secondary-400 mb-3">Owner: {pod.pod_owner_name} (<a href={`mailto:${pod.pod_owner_email}`} className="text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 underline transition-colors">{pod.pod_owner_email}</a>)</div>
                <div className="flex gap-8 mb-4">
                  <div>
                    <span className="block text-xs text-secondary-500 dark:text-secondary-400 mb-1">O2HAR</span>
                    <span className={`font-bold text-lg ${pod.o2har > 9.0 ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>{pod.o2har} Mins</span>
                  </div>
                  <div>
                    <span className="block text-xs text-secondary-500 dark:text-secondary-400 mb-1">Unserviceability</span>
                    <span className={`font-bold text-lg ${pod.unserviceability > 5.0 ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>{pod.unserviceability}%</span>
                  </div>
                </div>
                <label className="block text-sm text-secondary-700 dark:text-secondary-300 mb-2">Pod Owner Action Log</label>
                <textarea
                  className="w-full px-4 py-3 border border-neutral-200 dark:border-secondary-600 dark:bg-secondary-700/50 dark:text-white rounded-xl focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent placeholder-secondary-400 dark:placeholder-secondary-500"
                  rows={2}
                  value={pod.actionLog}
                  onChange={e => handlePodActionChange(pod.pod_id, e.target.value)}
                  placeholder="What is being done at the pod level?"
                />
              </div>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default CityPods;
