import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import CityCard from '../components/CityCard';
import DarkModeToggle from '../components/DarkModeToggle';
import ChatBot from '../components/ChatBot';
import { PodData, CitySummary } from '../types';
import { getCitySummaries } from '../utils/helpers';

const Dashboard: React.FC = () => {
  const [cities, setCities] = useState<CitySummary[]>([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [filteredCities, setFilteredCities] = useState<CitySummary[]>([]);
  const [chatOpen, setChatOpen] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await fetch('/data/pods.json');
        const podsData: PodData[] = await response.json();
        const citySummaries = getCitySummaries(podsData);
        setCities(citySummaries);
        setFilteredCities(citySummaries);
      } catch (error) {
        console.error('Error fetching pods data:', error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const filtered = cities.filter(city =>
      city.city.toLowerCase().includes(searchTerm.toLowerCase()) ||
      city.owner?.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredCities(filtered);
  }, [searchTerm, cities]);

  if (cities.length === 0) {
    return (
      <div className="min-h-screen bg-gradient-to-br from-neutral-50 to-primary-50 dark:from-neutral-900 dark:to-secondary-900">
        <div className="flex items-center justify-center min-h-screen">
          <div className="text-center">
            <div className="animate-spin rounded-full h-16 w-16 border-b-2 border-primary-500 mx-auto mb-4"></div>
            <p className="text-secondary-600 dark:text-secondary-400 text-lg">Loading your dashboard...</p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-neutral-50 to-primary-50 dark:from-neutral-900 dark:to-secondary-900">
      {/* Header */}
      <div className="bg-white/80 dark:bg-secondary-800/80 backdrop-blur-md border-b border-neutral-200 dark:border-secondary-700 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-14 h-14 bg-gradient-to-br from-primary-500 to-accent-500 rounded-2xl flex items-center justify-center shadow-medium">
                <span className="text-2xl text-white">🛒</span>
              </div>
              <div>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-primary-600 to-accent-600 bg-clip-text text-transparent">
                  Instamart Ops Co-Pilot
                </h1>
                <p className="text-secondary-600 dark:text-secondary-400 text-sm mt-1">
                  Real-time City Operations Dashboard
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className="text-right">
                <div className="text-sm text-secondary-500 dark:text-secondary-400">Last Updated</div>
                <div className="text-xs text-secondary-400 dark:text-secondary-500">{new Date().toLocaleTimeString()}</div>
              </div>
              <DarkModeToggle />
            </div>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div className="max-w-7xl mx-auto px-6 py-8">
        <div className="space-y-8">
          {/* Search and Quick Actions */}
          <div className="space-y-6">
            {/* Search Bar */}
            <div className="relative max-w-md">
              <input
                type="text"
                placeholder="Search cities or owners..."
                value={searchTerm}
                onChange={(e) => setSearchTerm(e.target.value)}
                className="w-full px-6 py-4 pl-14 bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm border border-neutral-200 dark:border-secondary-600 rounded-2xl shadow-soft focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent dark:text-white placeholder-secondary-400 dark:placeholder-secondary-500 transition-all duration-200"
              />
              <div className="absolute inset-y-0 left-0 pl-5 flex items-center pointer-events-none">
                <svg className="h-5 w-5 text-secondary-400" fill="none" viewBox="0 0 24 24" stroke="currentColor">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M21 21l-6-6m2-5a7 7 0 11-14 0 7 7 0 0114 0z" />
                </svg>
              </div>
            </div>

            {/* Quick Action Buttons */}
            <div className="flex flex-wrap gap-4">
              <button
                onClick={() => navigate('/alerts')}
                className="px-6 py-3 bg-gradient-to-r from-primary-500 to-primary-600 text-white rounded-xl shadow-soft hover:shadow-medium transition-all duration-200 font-medium flex items-center space-x-3 hover:scale-105"
              >
                <span className="text-lg">🔔</span>
                <span>Set Alerts</span>
              </button>
              <button
                onClick={() => setChatOpen(true)}
                className="px-6 py-3 bg-gradient-to-r from-secondary-500 to-secondary-600 text-white rounded-xl shadow-soft hover:shadow-medium transition-all duration-200 font-medium flex items-center space-x-3 hover:scale-105"
              >
                <span className="text-lg">💬</span>
                <span>Ask AI</span>
              </button>
              <button
                onClick={() => window.location.reload()}
                className="px-6 py-3 bg-gradient-to-r from-neutral-500 to-neutral-600 text-white rounded-xl shadow-soft hover:shadow-medium transition-all duration-200 font-medium flex items-center space-x-3 hover:scale-105"
              >
                <span className="text-lg">🔄</span>
                <span>Refresh Data</span>
              </button>
            </div>

            {/* Stats Summary */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl p-6 shadow-soft hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-secondary-500 dark:text-secondary-400 font-medium">Total Cities</p>
                    <p className="text-3xl font-bold text-primary-600 dark:text-primary-400">{cities.length}</p>
                  </div>
                  <div className="w-14 h-14 bg-primary-100 dark:bg-primary-900/30 rounded-xl flex items-center justify-center">
                    <span className="text-2xl">🏙️</span>
                  </div>
                </div>
              </div>
              <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl p-6 shadow-soft hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-secondary-500 dark:text-secondary-400 font-medium">Total Pods</p>
                    <p className="text-3xl font-bold text-success-600 dark:text-success-400">{cities.reduce((sum, city) => sum + city.totalPods, 0)}</p>
                  </div>
                  <div className="w-14 h-14 bg-success-100 dark:bg-success-900/30 rounded-xl flex items-center justify-center">
                    <span className="text-2xl">📦</span>
                  </div>
                </div>
              </div>
              <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl p-6 shadow-soft hover:shadow-medium transition-all duration-200 border border-neutral-200/50 dark:border-secondary-700/50">
                <div className="flex items-center justify-between">
                  <div>
                    <p className="text-sm text-secondary-500 dark:text-secondary-400 font-medium">Breaching Pods</p>
                    <p className="text-3xl font-bold text-error-600 dark:text-error-400">{cities.reduce((sum, city) => sum + city.breachedPods, 0)}</p>
                  </div>
                  <div className="w-14 h-14 bg-error-100 dark:bg-error-900/30 rounded-xl flex items-center justify-center">
                    <span className="text-2xl">⚠️</span>
                  </div>
                </div>
              </div>
            </div>
          </div>

          {/* City Cards */}
          <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
            {filteredCities.map(city => (
              <CityCard
                key={city.city}
                city={city.city}
                o2har={city.o2har}
                unserviceability={city.unserviceability}
                totalPods={city.totalPods}
                breachedPods={city.breachedPods}
                lastUpdated={city.lastUpdated}
                owner={city.owner}
                onClick={() => navigate(`/city/${city.city}`)}
              />
            ))}
          </div>

          {/* No Results Message */}
          {filteredCities.length === 0 && searchTerm && (
            <div className="text-center py-12">
              <div className="text-6xl mb-6">🔍</div>
              <p className="text-secondary-500 dark:text-secondary-400 text-lg mb-4">No cities found matching "{searchTerm}"</p>
              <button
                onClick={() => setSearchTerm('')}
                className="text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 underline font-medium"
              >
                Clear search
              </button>
            </div>
          )}
        </div>
      </div>

      <ChatBot isOpen={chatOpen} onToggle={() => setChatOpen(!chatOpen)} />
    </div>
  );
};

export default Dashboard;
