import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { PodData } from '../types';
import { getPodTrend, isMetricBreached } from '../utils/helpers';
import { THRESHOLDS } from '../utils/constants';
import ChatBot from '../components/ChatBot';
import MetricsChart from '../components/MetricsChart';
import CollapsibleSection from '../components/CollapsibleSection';

const PodDetail: React.FC = () => {
  const { podId } = useParams<{ podId: string }>();
  const navigate = useNavigate();
  const [, setPodTrend] = useState<PodData[]>([]);
  const [currentPod, setCurrentPod] = useState<PodData | null>(null);
  const [loading, setLoading] = useState(true);
  const [chatOpen, setChatOpen] = useState(false);

  useEffect(() => {
    const fetchPodData = async () => {
      try {
        const response = await fetch('/data/pods.json');
        const data = await response.json();
        const trend = getPodTrend(data, podId!);
        setPodTrend(trend);
        setCurrentPod(trend[0] || null);
      } catch (error) {
        console.error('Error fetching pod data:', error);
      } finally {
        setLoading(false);
      }
    };

    if (podId) {
      fetchPodData();
    }
  }, [podId]);

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-instamart-blue mx-auto mb-4"></div>
          <p className="text-gray-600 dark:text-gray-400">Loading pod details...</p>
        </div>
      </div>
    );
  }

  if (!currentPod) {
    return (
      <div className="min-h-screen bg-gray-50 dark:bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <p className="text-gray-600 dark:text-gray-400">Pod not found</p>
          <button
            onClick={() => navigate('/')}
            className="mt-4 px-4 py-2 bg-instamart-blue text-white rounded-lg"
          >
            Back to Dashboard
          </button>
        </div>
      </div>
    );
  }

  const o2harBreached = isMetricBreached('o2har', currentPod.o2har);
  const unserviceabilityBreached = isMetricBreached('unserviceability', currentPod.unserviceability);

  return (
    <div className="min-h-screen bg-gradient-to-br from-neutral-50 to-primary-50 dark:from-neutral-900 dark:to-secondary-900">
      {/* Header */}
      <div className="bg-white/80 dark:bg-secondary-800/80 backdrop-blur-md border-b border-neutral-200 dark:border-secondary-700 sticky top-0 z-10">
        <div className="max-w-7xl mx-auto px-6 py-6">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-4">
              <div className="w-14 h-14 bg-gradient-to-br from-primary-500 to-accent-500 rounded-2xl flex items-center justify-center shadow-medium">
                <span className="text-2xl text-white">📦</span>
              </div>
              <div>
                <button
                  onClick={() => navigate('/')}
                  className="text-secondary-600 dark:text-secondary-400 hover:text-primary-600 dark:hover:text-primary-400 mb-2 flex items-center text-sm font-medium transition-colors"
                >
                  ← Back to Dashboard
                </button>
                <h1 className="text-3xl font-bold bg-gradient-to-r from-primary-600 to-accent-600 bg-clip-text text-transparent">
                  {currentPod.pod_name}
                </h1>
                <p className="text-secondary-600 dark:text-secondary-400 text-sm mt-1">
                  {currentPod.zone}, {currentPod.city}
                </p>
                <p className="text-xs text-secondary-500 dark:text-secondary-500 mt-1">
                  Owner: {currentPod.pod_owner_name} (
                  <a 
                    href={`mailto:${currentPod.pod_owner_email}`} 
                    className="text-primary-600 hover:text-primary-700 dark:text-primary-400 dark:hover:text-primary-300 underline transition-colors"
                  >
                    {currentPod.pod_owner_email}
                  </a>
                  )
                </p>
              </div>
            </div>
            <div className="flex items-center space-x-4">
              <div className={`w-4 h-4 rounded-full shadow-md ${(o2harBreached || unserviceabilityBreached) ? 'bg-error-500' : 'bg-success-500'}`}></div>
            </div>
          </div>
        </div>
      </div>

      <div className="max-w-7xl mx-auto px-6 py-8">
        {/* Current Metrics */}
        <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 mb-6 border border-neutral-200/50 dark:border-secondary-700/50">
          <h2 className="text-xl font-bold text-secondary-900 dark:text-white mb-6">Current Metrics</h2>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
            <div className="text-center p-6 bg-neutral-50 dark:bg-secondary-700/50 rounded-xl hover:bg-neutral-100 dark:hover:bg-secondary-600/50 transition-colors">
              <p className="text-sm text-secondary-600 dark:text-secondary-400 mb-2 font-medium">O2HAR</p>
              <p className={`text-4xl font-bold ${o2harBreached ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>
                {currentPod.o2har} Mins
              </p>
              <p className="text-xs text-secondary-500 dark:text-secondary-400">Threshold: {THRESHOLDS.O2HAR} Mins</p>
              {o2harBreached && (
                <p className="text-xs text-error-600 dark:text-error-400 mt-2 font-medium">⚠️ Threshold Breached</p>
              )}
            </div>
            <div className="text-center p-6 bg-neutral-50 dark:bg-secondary-700/50 rounded-xl hover:bg-neutral-100 dark:hover:bg-secondary-600/50 transition-colors">
              <p className="text-sm text-secondary-600 dark:text-secondary-400 mb-2 font-medium">Unserviceability</p>
              <p className={`text-4xl font-bold ${unserviceabilityBreached ? 'text-error-600 dark:text-error-400' : 'text-success-600 dark:text-success-400'}`}>
                {currentPod.unserviceability}%
              </p>
              <p className="text-xs text-secondary-500 dark:text-secondary-400">Threshold: {THRESHOLDS.UNSERVICEABILITY}%</p>
              {unserviceabilityBreached && (
                <p className="text-xs text-error-600 dark:text-error-400 mt-2 font-medium">⚠️ Threshold Breached</p>
              )}
            </div>
          </div>
        </div>

        {/* Hourly Metrics Charts (Collapsible) */}
        {currentPod.hourly_metrics && currentPod.hourly_metrics_yesterday && currentPod.hourly_metrics_sdlw && (
          <div className="mb-6 space-y-4">
            <CollapsibleSection title="O2HAR Chart">
              <MetricsChart
                todayMetrics={currentPod.hourly_metrics}
                yesterdayMetrics={currentPod.hourly_metrics_yesterday}
                sdlwMetrics={currentPod.hourly_metrics_sdlw}
                metricType="o2har"
              />
            </CollapsibleSection>
            <CollapsibleSection title="Unserviceability Chart">
              <MetricsChart
                todayMetrics={currentPod.hourly_metrics}
                yesterdayMetrics={currentPod.hourly_metrics_yesterday}
                sdlwMetrics={currentPod.hourly_metrics_sdlw}
                metricType="unserviceability"
              />
            </CollapsibleSection>
          </div>
        )}

        {/* Root Causes & Recommendations */}
        {(o2harBreached || unserviceabilityBreached) && (
          <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft p-6 mb-6 border border-neutral-200/50 dark:border-secondary-700/50">
            <h2 className="text-xl font-bold text-secondary-900 dark:text-white mb-6">Analysis & Recommendations</h2>
            {/* Root Causes */}
            {currentPod.root_causes_details && (
              <div className="mb-6">
                <h3 className="text-md font-medium text-secondary-900 dark:text-white mb-4">Root Causes</h3>
                <div className="bg-error-50 dark:bg-error-900/20 border border-error-200 dark:border-error-700/50 rounded-2xl p-6">
                  <div className="space-y-4">
                    {currentPod.root_causes_details.map((cause, index) => (
                      <div key={index} className="flex items-center justify-between p-4 bg-white/50 dark:bg-secondary-800/50 rounded-xl border border-error-100 dark:border-error-800/50">
                        <div className="flex items-center space-x-3">
                          <div className="w-3 h-3 bg-error-500 rounded-full"></div>
                          <span className="text-error-800 dark:text-error-200 font-medium">{cause.description}</span>
                        </div>
                        <div className="text-right">
                          <div className="flex items-center space-x-2">
                            <span className="text-error-800 dark:text-error-200 font-semibold text-lg">{cause.actual} {cause.unit}</span>
                            <span className="text-error-600 dark:text-error-300 text-sm">vs expected</span>
                            <span className="text-error-800 dark:text-error-200 font-semibold text-lg">{cause.expected} {cause.unit}</span>
                            <span className={`ml-3 px-2 py-1 rounded-full text-xs font-semibold ${
                              cause.percent_diff > 0 
                                ? 'bg-error-100 dark:bg-error-800/50 text-error-700 dark:text-error-300' 
                                : 'bg-success-100 dark:bg-success-800/50 text-success-700 dark:text-success-300'
                            }`}>
                              {cause.percent_diff > 0 ? '+' : ''}{cause.percent_diff}%
                            </span>
                          </div>
                        </div>
                      </div>
                    ))}
                  </div>
                </div>
              </div>
            )}
            {/* Surge Table */}
            {currentPod.surge_table && (
              <div className="mb-6">
                <h3 className="text-md font-medium text-secondary-900 dark:text-white mb-4">Surge Pricing Recommendations</h3>
                <div className="overflow-x-auto">
                  <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft border border-neutral-200/50 dark:border-secondary-700/50 overflow-hidden">
                    <table className="min-w-full">
                      <thead className="bg-neutral-50 dark:bg-secondary-700/50">
                        <tr>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Cohort</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Current Price</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Recommended Price</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Price Change</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-neutral-200/50 dark:divide-secondary-700/50">
                        {currentPod.surge_table.map((row, index) => {
                          const priceChange = row.recommended_surge_rupees! - row.current_surge_rupees!;
                          const priceChangePercent = ((priceChange / row.current_surge_rupees!) * 100);
                          const isIncrease = priceChange > 0;
                          
                          return (
                            <tr key={index} className={`${index % 2 === 0 ? 'bg-white/50 dark:bg-secondary-800/50' : 'bg-neutral-50/50 dark:bg-secondary-700/50'} hover:bg-neutral-100/50 dark:hover:bg-secondary-600/50 transition-colors duration-200`}>
                              <td className="px-6 py-4 text-sm font-medium text-secondary-900 dark:text-white">
                                <div className="flex items-center space-x-2">
                                  <div className="w-2 h-2 bg-primary-500 rounded-full"></div>
                                  <span>{row.cohort}</span>
                                </div>
                              </td>
                              <td className="px-6 py-4 text-sm text-secondary-700 dark:text-secondary-300">
                                <div className="flex items-center space-x-2">
                                  <span className="text-lg font-semibold text-secondary-900 dark:text-white">₹{row.current_surge_rupees}</span>
                                  <span className="text-xs text-secondary-500 dark:text-secondary-400">per order</span>
                                </div>
                              </td>
                              <td className="px-6 py-4 text-sm text-secondary-700 dark:text-secondary-300">
                                <div className="flex items-center space-x-2">
                                  <span className="text-lg font-semibold text-secondary-900 dark:text-white">₹{row.recommended_surge_rupees}</span>
                                  <span className="text-xs text-secondary-500 dark:text-secondary-400">per order</span>
                                </div>
                              </td>
                              <td className="px-6 py-4 text-sm">
                                <div className="flex items-center space-x-2">
                                  <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold ${
                                    isIncrease 
                                      ? 'bg-error-100 dark:bg-error-900/30 text-error-700 dark:text-error-300' 
                                      : 'bg-success-100 dark:bg-success-900/30 text-success-700 dark:text-success-300'
                                  }`}>
                                    {isIncrease ? '↗' : '↘'} {isIncrease ? '+' : ''}{priceChangePercent.toFixed(0)}%
                                  </span>
                                  <span className={`text-xs font-medium ${
                                    isIncrease 
                                      ? 'text-error-600 dark:text-error-400' 
                                      : 'text-success-600 dark:text-success-400'
                                  }`}>
                                    {isIncrease ? '+' : ''}₹{priceChange}
                                  </span>
                                </div>
                              </td>
                            </tr>
                          );
                        })}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
            {/* Workforce Table */}
            {currentPod.workforce_table && (
              <div>
                <h3 className="text-md font-medium text-secondary-900 dark:text-white mb-4">Workforce Management</h3>
                <div className="overflow-x-auto">
                  <div className="bg-white/70 dark:bg-secondary-800/70 backdrop-blur-sm rounded-2xl shadow-soft border border-neutral-200/50 dark:border-secondary-700/50 overflow-hidden">
                    <table className="min-w-full">
                      <thead className="bg-neutral-50 dark:bg-secondary-700/50">
                        <tr>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Cohort</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">DEs Needed</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Current DEs</th>
                          <th className="px-6 py-4 text-left text-xs font-semibold text-secondary-600 dark:text-secondary-400 uppercase tracking-wider">Gap</th>
                        </tr>
                      </thead>
                      <tbody className="divide-y divide-neutral-200/50 dark:divide-secondary-700/50">
                        {currentPod.workforce_table.map((row, index) => (
                          <tr key={index} className={`${index % 2 === 0 ? 'bg-white/50 dark:bg-secondary-800/50' : 'bg-neutral-50/50 dark:bg-secondary-700/50'} hover:bg-neutral-100/50 dark:hover:bg-secondary-600/50 transition-colors duration-200`}>
                            <td className="px-6 py-4 text-sm font-medium text-secondary-900 dark:text-white">
                              <div className="flex items-center space-x-2">
                                <div className="w-2 h-2 bg-accent-500 rounded-full"></div>
                                <span>{row.cohort}</span>
                              </div>
                            </td>
                            <td className="px-6 py-4 text-sm text-secondary-700 dark:text-secondary-300">
                              <span className="text-lg font-semibold text-secondary-900 dark:text-white">{row.needed}</span>
                            </td>
                            <td className="px-6 py-4 text-sm text-secondary-700 dark:text-secondary-300">
                              <span className="text-lg font-semibold text-secondary-900 dark:text-white">{row.current}</span>
                            </td>
                            <td className="px-6 py-4 text-sm">
                              <div className="flex items-center space-x-2">
                                <span className={`inline-flex items-center px-3 py-1 rounded-full text-xs font-semibold ${
                                  row.gap > 0 
                                    ? 'bg-error-100 dark:bg-error-900/30 text-error-700 dark:text-error-300' 
                                    : 'bg-success-100 dark:bg-success-900/30 text-success-700 dark:text-success-300'
                                }`}>
                                  {row.gap > 0 ? '⚠' : '✓'} {row.gap > 0 ? '+' : ''}{row.gap}
                                </span>
                                <span className={`text-xs font-medium ${
                                  row.gap > 0 
                                    ? 'text-error-600 dark:text-error-400' 
                                    : 'text-success-600 dark:text-success-400'
                                }`}>
                                  {row.gap > 0 ? 'Shortage' : 'Sufficient'}
                                </span>
                              </div>
                            </td>
                          </tr>
                        ))}
                      </tbody>
                    </table>
                  </div>
                </div>
              </div>
            )}
          </div>
        )}

        {/* ChatBot */}
        <ChatBot isOpen={chatOpen} onToggle={() => setChatOpen(!chatOpen)} />
      </div>
    </div>
  );
};

export default PodDetail;
