import React, { useState } from 'react';

interface ChatBotProps {
  isOpen: boolean;
  onToggle: () => void;
}

interface Message {
  id: number;
  message: string;
  type: 'user' | 'bot';
}

const ChatBot: React.FC<ChatBotProps> = ({ isOpen, onToggle }) => {
  const [messages, setMessages] = useState<Message[]>([
    { id: 1, message: "Hello! I'm your Ops Co-Pilot. How can I help you today?", type: 'bot' }
  ]);
  const [inputMessage, setInputMessage] = useState('');

  const handleSendMessage = async () => {
    if (!inputMessage.trim()) return;

    const userMessage: Message = {
      id: messages.length + 1,
      message: inputMessage,
      type: 'user'
    };

    setMessages(prev => [...prev, userMessage]);
    setInputMessage('');

    // Simulate AI response
    setTimeout(() => {
      const botMessage: Message = {
        id: messages.length + 2,
        message: "I'm here to help with your operations questions. What specific metrics or issues would you like to discuss?",
        type: 'bot'
      };
      setMessages(prev => [...prev, botMessage]);
    }, 1000);
  };

  const handleKeyPress = (e: React.KeyboardEvent) => {
    if (e.key === 'Enter') {
      handleSendMessage();
    }
  };

  if (!isOpen) {
    return (
      <button
        onClick={onToggle}
        className="fixed bottom-6 right-6 w-16 h-16 bg-instamart-orange text-instamart-white rounded-2xl shadow-large hover:shadow-soft hover:scale-110 transition-all duration-300 flex items-center justify-center text-2xl z-50 border border-instamart-white/20"
      >
        ğŸ’¬
      </button>
    );
  }

  return (
    <div className="fixed bottom-6 right-6 w-80 h-96 bg-instamart-white/90 backdrop-blur-md rounded-2xl shadow-large border border-instamart-white/20 flex flex-col z-50">
      {/* Header */}
      <div className="bg-instamart-orange text-instamart-white p-4 rounded-t-2xl flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="w-8 h-8 bg-instamart-white/20 rounded-full flex items-center justify-center">
            <span className="text-sm">ğŸ¤–</span>
          </div>
          <h3 className="font-bold">Ops Co-Pilot</h3>
        </div>
        <button
          onClick={onToggle}
          className="text-instamart-white hover:text-instamart-white/80 transition-colors hover:bg-instamart-white/20 p-1 rounded-lg"
        >
          âœ•
        </button>
      </div>

      {/* Messages */}
      <div className="flex-1 p-4 overflow-y-auto space-y-3">
        {messages.map((message) => (
          <div
            key={message.id}
            className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
          >
            <div
              className={`max-w-xs px-4 py-3 rounded-2xl text-sm shadow-soft ${
                message.type === 'user'
                  ? 'bg-instamart-orange text-instamart-white'
                  : 'bg-neutral-100 text-neutral-900'
              }`}
            >
              {message.message}
            </div>
          </div>
        ))}
      </div>

      {/* Input */}
      <div className="p-4 border-t border-neutral-200/50">
        <div className="flex space-x-3">
          <input
            type="text"
            value={inputMessage}
            onChange={(e) => setInputMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder="Ask me anything..."
            className="flex-1 px-4 py-3 border border-neutral-200 bg-instamart-white/70 backdrop-blur-sm rounded-xl focus:outline-none focus:ring-2 focus:ring-instamart-orange focus:border-transparent placeholder-neutral-400"
          />
          <button
            onClick={handleSendMessage}
            className="px-4 py-3 bg-instamart-orange text-instamart-white rounded-xl hover:shadow-medium transition-all duration-200 font-medium hover:scale-105"
          >
            Send
          </button>
        </div>
      </div>
    </div>
  );
};

export default ChatBot;
