import React from 'react';
import { PodCardProps } from '../types';
import { getStatusColor, isMetricBreached } from '../utils/helpers';
import { THRESHOLDS } from '../utils/constants';

const PodCard: React.FC<PodCardProps> = ({ pod, onClick }) => {
  const statusColor = getStatusColor(pod);
  const o2harBreached = isMetricBreached('o2har', pod.o2har);
  const unserviceabilityBreached = isMetricBreached('unserviceability', pod.unserviceability);

  return (
    <div 
      className="bg-white rounded-lg shadow-md p-4 mb-4 cursor-pointer hover:shadow-lg transition-shadow duration-200"
      onClick={() => onClick(pod.pod_id)}
    >
      <div className="flex items-center justify-between mb-3">
        <div>
          <h3 className="text-lg font-semibold text-gray-900">{pod.pod_name}</h3>
          <p className="text-sm text-gray-600">{pod.zone}</p>
        </div>
        <div className={`w-3 h-3 rounded-full ${statusColor}`}></div>
      </div>
      
      <div className="grid grid-cols-2 gap-4 mb-3">
        <div className="text-center">
          <p className="text-xs text-gray-500 mb-1">O2HAR</p>
          <p className={`text-lg font-bold ${o2harBreached ? 'text-instamart-red' : 'text-instamart-green'}`}>
            {pod.o2har}
          </p>
          <p className="text-xs text-gray-400">Threshold: {THRESHOLDS.O2HAR}</p>
        </div>
        <div className="text-center">
          <p className="text-xs text-gray-500 mb-1">Unserviceability</p>
          <p className={`text-lg font-bold ${unserviceabilityBreached ? 'text-instamart-red' : 'text-instamart-green'}`}>
            {pod.unserviceability}%
          </p>
          <p className="text-xs text-gray-400">Threshold: {THRESHOLDS.UNSERVICEABILITY}%</p>
        </div>
      </div>
      
      {(o2harBreached || unserviceabilityBreached) && (
        <div className="bg-red-50 border border-red-200 rounded-md p-3">
          <p className="text-sm font-medium text-red-800 mb-2">Issues Detected:</p>
          <ul className="text-xs text-red-700 space-y-1">
            {pod.root_causes.map((cause, index) => (
              <li key={index} className="flex items-center">
                <span className="w-1 h-1 bg-red-500 rounded-full mr-2"></span>
                {cause}
              </li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default PodCard;
